const dates = [];
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1); 


for (let i = 0; i < 7; i++) {
  const date = new Date(yesterday);
  date.setDate(yesterday.getDate() - i); 
  dates.push({
    json: {
      date: date.toISOString().split('T')[0]
    }
  });
}

return dates;

