// Obter todos os dados da entrada
const items = $input.all();

// Verificar se há dados
if (items.length === 0) {
  return [];
}

// Extrair todas as datas únicas e formatá-las como 'DD/MM/YYYY'
const datas = [...new Set(items.map(item => {
  const date = new Date(item.json.dia);
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  return `${day}/${month}/${year}`;
}))].sort((a, b) => {
  // Converter de volta para data para ordenação correta
  const [dayA, monthA, yearA] = a.split('/').map(Number);
  const [dayB, monthB, yearB] = b.split('/').map(Number);
  return new Date(yearA, monthA - 1, dayA) - new Date(yearB, monthB - 1, dayB);
});

// Extrair todas as lojas únicas com unit_id e unit_name
const lojasSet = new Set();
items.forEach(item => {
  lojasSet.add(JSON.stringify({ unit_id: item.json.unit_id, unit_name: item.json.unit_name }));
});
const lojas = Array.from(lojasSet).map(str => JSON.parse(str)).sort((a, b) => {
  // Ordenar por unit_name em ordem ascendente
  return a.unit_name.localeCompare(b.unit_name, 'pt-BR', { sensitivity: 'base' });
});

// Criar um objeto para armazenar os dados pivotados
const pivotData = lojas.map(store => {
  const row = { unit_id: store.unit_id, unit_name: store.unit_name };
  datas.forEach(dia => {
    row[dia] = 0; // Inicializa com 0
  });
  return row;
});

// Preencher as vendas
items.forEach(item => {
  const store = item.json.unit_id;
  const storeName = item.json.unit_name;
  const date = new Date(item.json.dia);
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  const formattedDate = `${day}/${month}/${year}`;
  const venda = item.json.venda_do_dia;

  const row = pivotData.find(r => r.unit_id === store && r.unit_name === storeName);
  if (row) {
    // Formatar o número para substituir '.' por ',' e fixar duas casas decimais
    const vendaFormatada = Number(venda).toFixed(2).replace('.', ',');
    row[formattedDate] = vendaFormatada;
  }
});

// Preparar os cabeçalhos com 'unit_id', 'unit_name' e datas
const headers = ['unit_id', 'unit_name', ...datas];

// Preparar os dados para o Excel como uma matriz de objetos
const excelData = pivotData.map(row => {
  const newRow = {};
  headers.forEach(header => {
    newRow[header] = row[header];
  });
  return { json: newRow };
});

// Retornar os dados pivotados como uma matriz de objetos
return excelData;

